html:
{% set member = frappe.get_doc("Gym Member", doc.gym_member) %}

<style>
.card {
    width: 450px;
    height: 260px;
    padding: 15px;
    background: url("/assets/gym_management/files/gym_background_image3.jpeg") no-repeat center center;
    background-size: cover;
    border-radius: 12px;
    color: white;
    font-family: Arial, sans-serif;
    position: relative;
}

/* Member Photo */
.photo {
    width: 90px;
    height: 90px;
    border: 2px solid #fff;
    border-radius: 8px;
    overflow: hidden;
    float: left;
}
.photo img {
    width: 90px;
    height: 90px;
}

/* Details */
.details {
    margin-left: 110px;
    margin-top: 5px;
    color: white;
}
.details h2 {
    margin: 0px;
    font-size: 20px;
    font-weight: bold;
    color: white;
}
.details p {
    margin: 3px 0;
    font-size: 14px;
    color: white;
}
b{
    margin: 3px 0;
    font-size: 14px;
    color: white;
}

/* QR Section */
.qr {
    position: absolute;
    bottom: 10px;
    right: 10px;
}
.qr img {
    width: 80px;
    height: 80px;
}
</style>


<div class="card">

    <!-- PHOTO -->
    <div class="photo">
        {% if member.member_photo %}
            <img src="{{ member.member_photo }}">
        {% else %}
            <img src="/assets/gym_management/files/gym_user_photo_default.jpg">
        {% endif %}
    </div>

    <!-- DETAILS -->
    {% set plan = frappe.get_doc("Gym Membership Plan", doc.gym_member_plan_type) %}
    <div class="details">
        <h2>{{ member.member_name }}</h2>

        <p><b>Member ID:</b> {{ member.member_id }}</p>
        <p><b>Plan:</b> {{ plan.plan_name }}</p>
        <p><b>Start date:</b> {{ doc.get_formatted('start_date')}}</p>
        <p><b>Expiry date:</b> {{ doc.get_formatted('expiry_date')}}</p>
        <p><b>Status:</b> {{ doc.status }}</p>
    </div>

    <!-- QR CODE -->
    {% set qr_data = (
        "Name: " ~ member.member_name ~ "\n" ~
        "Membership ID: " ~ member.member_id ~ "\n" ~
        "Plan: " ~ doc.gym_member_plan_type ~ "\n" ~
        "Start Date: " ~ doc.start_date ~ "\n" ~
        "Expiry Date: " ~ doc.expiry_date ~ "\n" ~
        "Status: " ~ doc.status
    )
%}

<div class="qr">
    <img src="https://api.qrserver.com/v1/create-qr-code/?data={{ qr_data | urlencode }}&size=80x80">
</div>


</div>
